import Home from '../views/home/index'
import Tabbar from '../components/tab-bar/index'
import Navbar from '../components/nav-bar/index'
import Category from '../views/category/index'
import Cart from '../views/cart/index'
import Mine from '../views/min/index'
import Detail from '../views/detail/index'
import subCategory from '../views/category/sub-category'
const routes =[
    {
        path:'/',
        redirect:'/home'
    },
    {
        path:'/home',
        name:'home',
        components:{
            //header: Navbar, //告知该路由头部应该展示那个组件
            default:Home,
            footer:Tabbar,
        },
        meta:{ //路由其它基本信息 
            //这里面的其它信息都可以进行在定义。
            icon:'wap-home-o',
            title:'首页',
            inTabbar: true ,
        }
    },
    {
        path:'/detail/:id',
        name:'detail',
        components:{
            header: Navbar, //告知该路由头部应该展示那个组件
            default:Detail,
            //footer:Tabbar,
        },
        meta:{ //路由其它基本信息 
            //这里面的其它信息都可以进行在定义。
            icon:'wap-home-o',
            title:'商品详情',
            inTabbar: false ,
        }
    },
    {
        path:'/category',
        name:'category',
        components:{
            //header: NavBar //告知该路由头部应该展示那个组件
            header:Navbar,
            default:Category,
            footer:Tabbar,
        },
        children:[ //子路由
            {
                path:'sub/:id',// /category/sub/1
                name:'category.sub',
                component:subCategory,
                meta:{
                    title:'子分类',
                }
            }

        ],
        meta:{ //路由其它基本信息 
            //这里面的其它信息都可以进行在定义。
            icon:'wap-home-o',
            title:'分类',
            inTabbar: true ,
        }
    },
    {
        path:'/cart',
        name:'cart',
        components:{
            //header: NavBar //告知该路由头部应该展示那个组件
            header:Navbar,
            default:Cart,
           // footer:Tabbar,
        },
        meta:{ //路由其它基本信息 
            //这里面的其它信息都可以进行在定义。
            icon:'wap-home-o',
            title:'购物车',
            inTabbar: true ,
        }
    },
    {
        path:'/min',
        name:'min',
        components:{
            //header: NavBar //告知该路由头部应该展示那个组件
            header:Navbar,
            default:Mine,
            footer:Tabbar,
        },
        meta:{ //路由其它基本信息 
            //这里面的其它信息都可以进行在定义。
            icon:'wap-home-o',
            title:'我的',
            inTabbar: true ,
        }
    },
]
export default routes
